    },
    fbAdminCategoryText(c) {
      const v = String(c || '');
      if (v === 'env') return 'ç¯å¢ƒè¯„ä»·';
      if (v === 'service') return 'æœåŠ¡è¯„ä»·';
      if (v === 'suggestion') return 'å»ºè®®';
      if (v === 'complaint') return 'æŠ•è¯‰';
