    /* seat methods */
    initSeatGrid() {
      const rows = 6;
      const cols = 8;
      const grid = [];
      let id = 1;
      for (let r = 0; r < rows; r++) {
        const row = {
          rowIndex: r,
          seats: []
        };
        for (let c = 0; c < cols; c++) {
          row.seats.push({
            id: id++,
            row: r,
            col: c,
            label: `${r + 1}-${c + 1}`,
            status: 'free',
            selected: false
          });
        }
        grid.push(row);
      }
      grid[0].seats[0].status = 'occupied';
      grid[0].seats[1].status = 'occupied';
      grid[2].seats[3].status = 'disabled';
      grid[3].seats[4].status = 'disabled';
      this.seatGrid = grid;
    },
