    formatYmdhm(dtStr) {
      if (!dtStr) return '-';
      const s = String(dtStr).replace('T', ' ');
      const parts = s.split(' ');
      const d = parts[0] || '';
      const t = (parts[1] || '').slice(0, 5);
      if (!d) return s;
      const ds = d.split('-');
      if (ds.length >= 3) {
        const mmdd = `${String(ds[1]).padStart(2, '0')}-${String(ds[2]).padStart(2, '0')}`;
        return t ? `${mmdd} ${t}` : mmdd;
      }
      return s;
    },
