import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.some.js";
import "core-js/modules/esnext.json.parse.js";
export default {
  name: 'AppHome',
  props: {
    currentPage: {
      type: String,
      required: true
    }
  },
  emits: ['change-page'],
  data() {
    return {
      // 场地（地点）列表
      reserveVenues: ['本部 · 图书馆 301', '本部 · 图书馆 401', '东校区 · 教学楼 3 楼'],
      reserveVenueIndex: 0,
      // 日期相关
      dateList: [],
      visibleStart: 0,
      visibleCount: 7,
      // 一屏展示多少天
      currentDateIndex: 0,
      // 时间段
      timeSlots: [],
      // 示例：部分时间段不可预约
      disabledSlotIds: ['0', '1', '2', '3', '4', '5', '23'],
      // 已选时段（最多 4 个）
      selectedSlots: [],
      // 是否只展示“可预约”的时段
      onlyShowAvailable: false,
      // 房间 id 映射（按顺序和 reserveVenues 对应）
      roomIds: [1, 2, 3],
      // 改成你数据库 room 表里的真实 id

      // 后端返回的“已满时段”
      disabledSlotIds: [],
      // 当前登录用户 id（从登录时存的用户信息里读）
      currentUserId: null
    };
  },
  computed: {
    visibleDates() {
      return this.dateList.slice(this.visibleStart, this.visibleStart + this.visibleCount).map((d, idx) => ({
        ...d,
        index: this.visibleStart + idx
      }));
    },
    canMovePrev() {
      return this.visibleStart > 0;
    },
    canMoveNext() {
      return this.visibleStart + this.visibleCount < this.dateList.length;
    },
    currentVenueName() {
      return this.reserveVenues[this.reserveVenueIndex] || '';
    },
    // 当前选中的日期完整文本（用于右侧概要）
    currentDateLabel() {
      const cur = this.dateList[this.currentDateIndex];
      return cur ? cur.fullLabel : '';
    },
    // 根据开关过滤时间段列表
    filteredTimeSlots() {
      if (!this.onlyShowAvailable) {
        return this.timeSlots;
      }
      // 只展示“可预约”的时段
      return this.timeSlots.filter(slot => this.slotState(slot) === 'available');
    },
    currentRoomId() {
      return this.roomIds[this.reserveVenueIndex];
    },
    // 后端需要 yyyy-MM-dd 格式的日期字符串
    currentDateStr() {
      const cur = this.dateList[this.currentDateIndex];
      // 这里要用你 dateList 里真正存 API 用日期的字段
      // 比如你是 { apiDate: '2025-12-13', displayDate: '2025-12-13' }
      return cur ? cur.apiDate || cur.fullLabel || cur.dateStr : null;
    }
  },
  created() {
    this.timeSlots = this.buildTimeSlots();
    this.initDates();
    this.initDates();
    this.initTimeSlots();

    // 读取当前登录用户
    const raw = sessionStorage.getItem('user');
    if (raw) {
      try {
        const user = JSON.parse(raw);
        this.currentUserId = user.id; // 按你后端 User 的字段名改
      } catch (e) {
        console.error(e);
      }
    }
  },
  methods: {
    emitChange(page) {
      this.$emit('change-page', page);
    },
    // 构造 24 个时间段（0-1 点，1-2 点...）
    buildTimeSlots() {
      const list = [];
      for (let h = 0; h < 24; h++) {
        const next = (h + 1) % 24;
        const id = String(h);
        const range = `${h.toString().padStart(2, '0')}:00 - ` + `${next.toString().padStart(2, '0')}:00`;
        const label = (h % 12 === 0 ? 12 : h % 12) + ': 00 ' + (h < 12 ? 'AM' : 'PM');
        list.push({
          id,
          label,
          range
        });
      }
      return list;
    },
    // 生成接下来 14 天的日期条
    initDates() {
      const today = new Date();
      const list = [];
      const weekdays = ['周日', '周一', '周二', '周三', '周四', '周五', '周六'];
      for (let i = 0; i < 14; i++) {
        const d = new Date(today);
        d.setDate(d.getDate() + i);
        const month = d.getMonth() + 1;
        const day = d.getDate();
        const fullLabel = `${d.getFullYear()}-${month.toString().padStart(2, '0')}-` + `${day.toString().padStart(2, '0')}`;
        list.push({
          key: i,
          year: d.getFullYear(),
          month,
          day,
          weekday: weekdays[d.getDay()],
          monthLabel: `${d.getFullYear()}-${month.toString().padStart(2, '0')}`,
          isToday: i === 0,
          isTomorrow: i === 1,
          fullLabel
        });
      }
      this.dateList = list;
      this.visibleStart = 0;
      this.currentDateIndex = 0;
    },
    moveDates(direction) {
      if (direction === 'prev' && this.canMovePrev) {
        this.visibleStart -= 1;
      } else if (direction === 'next' && this.canMoveNext) {
        this.visibleStart += 1;
      }
    },
    selectDate(index) {
      this.currentDateIndex = index;
    },
    buildKey(dateIndex, slotId) {
      return `${dateIndex}-${slotId}`;
    },
    // 当前时段是可预约 / 已选 / 不可预约
    slotState(slot) {
      if (this.disabledSlotIds.includes(slot.id)) {
        return 'disabled';
      }
      const key = this.buildKey(this.currentDateIndex, slot.id);
      const exists = this.selectedSlots.some(item => item.key === key);
      if (exists) {
        return 'selected';
      }
      return 'available';
    },
    slotClass(slot) {
      const state = this.slotState(slot);
      return {
        'slot-available': state === 'available',
        'slot-disabled': state === 'disabled',
        'slot-selected': state === 'selected'
      };
    },
    // 点击时段：选中 / 取消
    toggleSlot(slot) {
      const key = this.buildKey(this.currentDateIndex, slot.id);
      const index = this.selectedSlots.findIndex(item => item.key === key);
      if (index !== -1) {
        this.selectedSlots.splice(index, 1);
        return;
      }
      if (this.selectedSlots.length >= 4) {
        window.alert('最多只能选择 4 个预约时段');
        return;
      }
      const dateInfo = this.dateList[this.currentDateIndex];
      const dateLabel = dateInfo.fullLabel;
      this.selectedSlots.push({
        key,
        dateIndex: this.currentDateIndex,
        dateLabel,
        slot
      });
    },
    removeSelected(key) {
      const index = this.selectedSlots.findIndex(item => item.key === key);
      if (index !== -1) {
        this.selectedSlots.splice(index, 1);
      }
    },
    // 这里仍然保持“跳到我的预约页面”的原来逻辑
    async submitReservations() {
      if (!this.selectedSlots.length) return;
      if (!this.currentUserId) {
        this.$message && this.$message.error('请先登录后再预约');
        return;
      }
      const slotIds = this.selectedSlots.map(item => Number(item.slot.id));
      try {
        const resp = await this.$axios.post('/reservation/create', {
          userId: this.currentUserId,
          // 现在先简单一点从前端传过去
          roomId: this.currentRoomId,
          date: this.currentDateStr,
          slotIds
        });
        const result = resp.data;
        if (result.code && result.code !== 200) {
          // 这里的判断根据你的 Result 修改
          this.$message && this.$message.error(result.msg || '预约失败');
          return;
        }
        this.$message && this.$message.success('预约成功');
        // 清空选择
        this.selectedSlots = [];
        // 重新拉一遍时段状态，刷新灰色/可选状态
        await this.fetchSlotStatus();
      } catch (e) {
        console.error(e);
        this.$message && this.$message.error('预约失败，服务器异常');
      }
    },
    resetSelections() {
      this.selectedSlots = [];
    },
    async fetchSlotStatus() {
      // 房间或日期还没准备好就先不调
      if (!this.currentRoomId || !this.currentDateStr) return;
      try {
        const resp = await this.$axios.get('/reservation/slots', {
          params: {
            roomId: this.currentRoomId,
            date: this.currentDateStr
          }
        });

        // 根据你的 Result 类结构取 data，这里假设是 { code, msg, data }
        const result = resp.data;
        const dto = result.data || {};

        // 后端返回的是 [8,9,14] 这样，我们前端习惯用字符串 id
        this.disabledSlotIds = (dto.disabledSlotIds || []).map(id => String(id));
      } catch (e) {
        console.error(e);
        this.$message && this.$message.error('获取时段状态失败');
      }
    }
  }
};
